import React, { useEffect, useRef, useState } from "react";
import {
  View,
  Text,
  StyleSheet,
  TouchableOpacity,
  Image,
  FlatList,
  Dimensions,
  ScrollView,
  PanResponder,
  Alert,
  Modal,
  Button,
  Animated,
} from "react-native";
import HeaderComponent from "../Components/HeaderScreen";
import { useDispatch, useSelector } from "react-redux";
import { planCategoryUsingSpot, planCategoryDeleteSpot } from "../../apis/planapi"

const categorys = [
  {
    "dataCode":"0",
    "dataName":"리안",
    "count":1
  },
  {
    "dataCode":"1",
    "dataName":"마왕족발",
    "count":1
  },
  {
    "dataCode":"2",
    "dataName":"김치찜은 못참치",
    "count":1
  },
  {
    "dataCode":"3",
    "dataName":"최각루",
    "count":2
  },
  {
    "dataCode":"4",
    "dataName":"피자가좋다",
    "count":12
  },
  {
    "dataCode":"5",
    "dataName":"세찜",
    "count":3
  },
  {
    "dataCode":"6",
    "dataName":"내가닭이다",
    "count":3
  },
  {
    "dataCode":"7",
    "dataName":"52는맛있지",
    "count":13
  },
  {
    "dataCode":"8",
    "dataName":"최코다리",
    "count":2
  },
  {
    "dataCode":"9",
    "dataName":"신촌 등갈비찜",
    "count":5
  },
  {
    "dataCode":"10",
    "dataName":"우정양개탕",
    "count":4
  },
  {
    "dataCode":"11",
    "dataName":"다이소다이소",
    "count":3
  },
];

export default function CategorySettingScreen({ navigation, route }) {
  // 토큰
  const grantType =  useSelector((state)=>state.user.grantType)
  const accessToken =  useSelector((state)=>state.user.accessToken)
  const refreshToken =  useSelector((state)=>state.user.refreshToken)
  const dispatch = useDispatch()
  // 그 외
  const [categorySeq, setCategorySeq] = useState(route.params.categorySeq);
  const [categoryTitle, setCategoryTitle] = useState(route.params.categoryTitle)
  const [isDeleteModalVisible, setIsDeleteModalVisible] = useState(false);
  const [selectedSpot, setSelectedSpot] = useState(null)


  
  // categorySeq와 일치하는 category 데이터를 찾습니다.
  // const [categorySpot, setCategorySpot] = useState(categorys)
  const [categorySpot, setCategorySpot] = useState(null)

  // 카테고리 사용처 목록 가져오기 함수
  const doPlanCategoryUsingSpot = async () => {
    try{
      const response = await planCategoryUsingSpot(categorySeq, accessToken, grantType)
      if(response.status===200){
        console.log("카테고리 사용처 목록 데이터:",response.data)
        // setCategorySpot(response.data)
        console.log('카테고리 사용처 목록 가져오기 성공') 
      }else{
        console.log('카테고리 사용처 목록 가져오기 실패',response.status) 
      } 
    }catch(error){
      console.log('카테고리 사용처 목록 가져오기 실패',error) 
    }
  }

  const doPlanCategoryDeleteSpot = async (dataCode) => {
    try{
      const response = await planCategoryDeleteSpot(dataCode, accessToken, grantType)
      if(response.status===200){
        // setCategorySpot(response.data)
        console.log('카테고리 사용처 삭제하기 성공') 
      }else{
        console.log('카테고리 사용처 삭제하기 실패',response.status) 
      } 
    }catch(error){
      console.log('카테고리 사용처 삭제하기 실패',error) 
    }
  }


  

  useEffect(()=>{
    doPlanCategoryUsingSpot()
  },[])

  return (
    <View style={styles.container}>
      <HeaderComponent title="카테고리 세부 내용" navigation={navigation}></HeaderComponent>

      <View style={styles.topContainer}>
        {/* 페이지 상단 좌측에 categoryTitle 출력 */}
        <View style={styles.categoryTitleContainer}>
          <Text style={styles.categoryTitle}>{categoryTitle}</Text>
          <Text style={styles.categoryTitleExplain}>
            자주 사용하는 사용처를 분류해주세요.
          </Text>
        </View>
        <Image
          style={styles.checkIcon}
          source={require("../../assets/images/Hamster/CheckHamster.png")}
        />
      </View>

      {/* 자주 사용하는 사용처 */}
      <View style={styles.categoryGroup}>
        <Text style={styles.categoryGroupText}>자주 사용하는 사용처</Text>
      </View>
      {/* 카테고리 목록 */}
      {categorySpot
        ?(<View style={{ flexDirection: 'row', flexWrap: 'wrap' }}>
          {categorySpot.map((item, index) => {
            if(item.count > 1){
              return(
                <TouchableOpacity
                  key={index}
                  style={[
                    styles.categoryItem,
                  ]}
                  onPress={()=>{
                    setIsDeleteModalVisible(true)
                    setSelectedSpot({dataCode:item.dataCode, dataName:item.dataName})
                  }}
                >
                  <Text style={styles.categoryItemText}>{item.dataName}</Text>
                </TouchableOpacity>
              )
            }else{
              return null
            }
          })}
        </View>)
        : (<View style={{ flexDirection: 'row', flexWrap: 'wrap' }}></View>)
      }
      {/* 구분선 */}
      <View style={styles.separator}></View>
      {/* 신규 사용처 */}
      <View style={styles.categoryGroup}>
        <Text style={styles.categoryGroupText}>신규 사용처</Text>
      </View>
      {/* 신규 카테고리 목록 */}
      {categorySpot
        ?(<View style={{ flexDirection: 'row', flexWrap: 'wrap' }}>
          {categorySpot.map((item, index) => {
            if(item.count ==1){
              return(
                <TouchableOpacity
                  key={index}
                  style={[
                    styles.categoryItem,
                  ]}
                  onPress={()=>{
                    setIsDeleteModalVisible(true)
                    setSelectedSpot({dataCode:item.dataCode, dataName:item.dataName})
                  }}
                >
                  <Text style={styles.categoryItemText}>{item.dataName}</Text>
                </TouchableOpacity>
              )
            }else{
              return null
            }
          })}
        </View>)
        : (<View style={{ flexDirection: 'row', flexWrap: 'wrap' }}></View>)
      }

      {/* 카테고리 사용처 삭제 모달 */}
      {isDeleteModalVisible
        ?(<View style={styles.centeredView}>
          <Modal
            animationType="none"
            transparent={true}
            visible={isDeleteModalVisible}
            onRequestClose={() => {
              // Alert.alert('Modal has been closed.');
              setIsDeleteModalVisible(false)
              setSelectedSpot(null)
            }}>
            <View style={styles.centeredView}>
              <View style={styles.modalView}>
                <Text style={styles.modalText}>{selectedSpot.dataName}를 {categoryTitle} 카테고리에서 삭제 하시겠습니까?</Text>
                <View style={{flexDirection:'row', justifyContent:'space-around'}}>
                  <TouchableOpacity
                    style={[styles.button, styles.buttonClose]}
                    onPress={() => {
                      // doPlanCategoryDeleteSpot(selectedSpot.dataCode)
                      const newCategorySpot = categorySpot.filter(item => item.dataCode !== selectedSpot.dataCode);
                      setCategorySpot(newCategorySpot)
                      setIsDeleteModalVisible(false)
                      setSelectedSpot(null)
                    }}>
                    <Text style={styles.textStyle}>예</Text>
                  </TouchableOpacity>
                  <TouchableOpacity
                    style={[styles.button, styles.buttonClose]}
                    onPress={() => {
                      setIsDeleteModalVisible(false)
                      setSelectedSpot(null)
                    }}>
                    <Text style={styles.textStyle}>아니오</Text>
                  </TouchableOpacity>
                </View>
              </View>
            </View>
          </Modal>
        </View>)
        :null
      }
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "white",
    padding: 16,
  },
  header: {
    flexDirection: "row",
    alignItems: "center",
    marginBottom: 16,
    marginTop: 15,
  },
  arrowIcon: {
    width: 20,
    height: 20,
  },
  topContainer: {
    flexDirection: "row",
    // alignContent: "place-between",
    marginLeft: "5%",
    // width: "90%",
    marginTop: 20,
    marginBottom: 50,
  },
  categoryTitleContainer: {
    flexDirection: "column",
  },
  categoryTitle: {
    fontSize: 20,
    fontWeight: "bold",

    flex: 1,
    textAlign: "left",
    marginLeft: 8, // categoryTitle을 뒤에 추가
    marginTop: 70,
    marginBottom: -10,
  },
  categoryTitleExplain: {
    fontSize: 10,
    flex: 1,
    textAlign: "left",
    marginLeft: 8, // categoryTitle을 뒤에 추가
    color: "#7B7B7B",
  },
  categoryGroup: {
    flexDirection: "row",
    alignItems: "center",
    marginBottom: 20,
  },
  categoryGroupText: {
    fontSize: 20,
    fontWeight: "bold",
    marginLeft: "5%",
    marginBottom: 10,
  },
  checkIcon: {
    width: 150,
    height: 150,
    marginLeft: 10,
  },
  categoryList: {
    flexDirection: "column", // 세로로 배치
    alignSelf: "left",
  },
  categoryItem: {
    alignItems: "center",
    padding: 4,
    marginBottom: 8,
    marginHorizontal: 8, // 좌우 여백 추가
    borderWidth: 1, // 테두리 추가
    borderColor: "#7B7B7B", // 테두리 색상
    borderRadius: 20, // 테두리 둥글게 설정
  },
  categoryItemText: {
    fontSize: 16,
    marginHorizontal: 5,
  },
